pragma solidity ^0.5.16;

contract Election {
    // Model a Answer
    struct Answer {
        uint id;
        string name;
        uint voteCount;
    }

    string public question;

    bool public hasQuestioned = false;
    // Store accounts that have voted
    mapping(address => bool) public voters;
    // Store Answers
    // Fetch Answer
    mapping(uint => Answer) public answers;
    // Store answers Count
    uint public answersCount;

    // voted event
    event votedEvent (
        uint indexed _answerId
    );

    event setQuestionEvent (
        string _questionId
    );

    constructor() public {
        question = "";
        addAnswer("Ahmad");
        addAnswer("Ammar");
    }

    function addAnswer (string memory _name) private {
        answersCount ++;
        answers[answersCount] = Answer(answersCount, _name, 0);
    }

    function setQuestion (string memory _questionId) public {
        question = _questionId;
        hasQuestioned = true;
        emit setQuestionEvent(_questionId);
    }


    function vote (uint _answerId) public {
        // require that they haven't voted before
        require(!voters[msg.sender]);

        // require a valid answer
        require(_answerId > 0 && _answerId <= answersCount);

        // record that voter has voted
        voters[msg.sender] = true;

        // update answer vote Count
        answers[_answerId].voteCount ++;

        // trigger voted event
        emit votedEvent(_answerId);
    }
}
